class HadithModel {
  HadithModel({
    required this.category,
    required this.array,
  });
  late final String category;
  late final List<HadithArray> array;

  HadithModel.fromJson(Map<String, dynamic> json) {
    category = json['category'];
    array =
        List.from(json['array']).map((e) => HadithArray.fromJson(e)).toList();
  }

  Map<String, dynamic> toJson() {
    final data = <String, dynamic>{};
    data['category'] = category;
    data['array'] = array.map((e) => e.toJson()).toList();
    return data;
  }

